import React from 'react';
import BurgerMenu from '../burger-menu';
import './header.css';

class Header extends React.Component {
    state = {
        open: false
    };
    onMenuClickHandler = () => {
        this.setState((state) => {
            return {
                open: !state.open
            };
        });
    };
    render() {
        const { open } = this.state;
        let classNames = {
            logIn: "log-in__menu",
            currency: "currency__menu",
            lang: "lang__menu",
        };
        if (open) {
            classNames += "  open"
        }
        return (
           <header className="header">
               <nav className="header-top main-nav">
                   <BurgerMenu/>
                    <ul className="menu">
                        <li className="menu-item log-in" onClick= { this.onMenuClickHandler }>Личный кабинет
                            <ul className={ open? null:"log-in__menu"}>
                                <li>Вход</li>
                                <li>Регистрация</li>
                            </ul>
                        </li>
                        <li className="menu-item currency" onClick= { this.onMenuClickHandler }>Рубль
                            <ul className={open? null:classNames.currency}>
                                <li>Euro</li>
                                <li>Dollar</li>
                            </ul>
                        </li>
                        <li className="menu-item lang" onClick= { this.onMenuClickHandler }>Ru
                            <ul className={classNames.lang}>
                                <li>En</li>
                            </ul>
                        </li>
                        <li className="menu-item telephone">8-800-500-61-01<br/><b>Круглосуточная поддержка</b></li>
                    </ul>
               </nav>
               <div className="header-middle">
                   <a className="header__logo" href="https://yandex.ru">
                      <img src="https://s4.aconvert.com/convert/p3r68-cdx67/cb04a-0b7eq.svg" alt=""/>
                   </a>
                   <h3>Морские прогулки</h3>
                   <h2>по черному морю</h2>
                   <a href="https://yandex.ru" className="header-middle__button">Выбрать круиз</a>
               </div>
           </header>
        )
    }
};
export default Header;
