   /*
    render() {
        return (
            <article className="card-item">
                <div className="cards-image__container">
                    <img className="cards-image" src={require('./img/test.png')} alt="anapa"/>
                </div>
                <section className="card-item__description">
                    <h4 className="description-name">ppp</h4>
                    <p className="track-shedule"><b>Маршрут:</b>{}</p>
                    <p className="duration"><b>Продолжительность:</b>{}</p>
                    <p className="price">27 000 руб</p>
                </section>
            </article>
        )
    }
    */
    constructor(data) {
        super();
        this.title = data.name;
        this.track = data.tracking;
    }
   render() {
        return (<article className="card-item">
                <div className="cards-image__container">
                    <img className="cards-image" src={require('./img/test.png')} alt="anapa"/>
                </div>
                <section className="card-item__description">
                    <h4 className="description-name">{ this.title }</h4>
                    <p className="track-shedule"><b>Маршрут:</b>{ this.track }</p>
                    <p className="duration"><b>Продолжительность:</b>{}</p>
                    <p className="price">27 000 руб</p>
                </section>
            </article>)
    }
};
