import React from 'react';
import Card from '../card';
import ArrDataOfCards from '../generate-data';
//import PopUp from '../pop-up';
import './main.css';

const sortCards = (filter,arr) => {
    const neewwwwww = arr.slice();
   if (filter === 'to-expensive') {
    neewwwwww.sort((a, b) => {
        return (+a.price) - (+b.price)
    });
   } else if (filter === 'to-cheap') {
    neewwwwww.sort((a,b) => {
           return (+b.price) - (+a.price)
       });
   } else if(filter === 'most-popular') {
    neewwwwww.sort((a,b) =>{
           return b.popular - a.popular
       });
   } else {
       return neewwwwww;
   }
   return neewwwwww;
};

export default class Main extends React.Component {
    constructor() {
        super();
        this.superddata = ArrDataOfCards;
    }
    state = {
        amountCard: 4,
        popup: false,
        filter: 'none',
    }
    increaseAmountOfCards = () => { 
        this.setState({amountCard: this.state.amountCard + 2});
        //console.log(this.state.amountCard,'state');
        if (this.state.amountCard === 8) {
            //console.log(this.state.amountCard,'if === ready!!!!!!');
            this.setState({amountCard: this.state.amountCard - 4});
        };
    };
    
    //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
    readynewCards = (arr,amountOfCards) => {
        console.log('test');
        const  newarr = arr.slice(0, amountOfCards);
        return (newarr.map((item) => {
                console.log(item);
                return <Card dataOfEachCard = {item} key = {item.id}/>//передаем состояние в карточку
            })
        );
    };
    returnValue = () => {
        let arrOfOptions = document.querySelectorAll('option');
        for (let Item of arrOfOptions) {
            if (Item.selected) {
                console.log(this.state.filter);
                this.setState({filter: Item.value});
                return Item.value;
            };
        };
    };
    
    render() {
        let { amountCard, popup, filter} = this.state;
        const arrFoRender = this.superddata.slice();
        const timePerem = 'most-popular';
        const rrr = sortCards(filter,arrFoRender);
        console.log(rrr,'in render');
        return (<div className="main">
                <h2 className="main__title">Каталог круизов</h2>
                <h3 className="main__subtitle" onClick={this.returnValue}>Сортировать круизы по
                    <select className="main__filter">
                        <option value="to-expensive" className="price-cheap">цене (по возрастанию)</option>
                        <option value="to-cheap" className="price-expensive">цене (по убыванию)</option>
                        <option value="most-popular" className="card-popular">популярности</option>
                    </select>
                </h3>
                <div className="cards-container">{this.readynewCards(rrr,amountCard)}</div>
                <button className="cards-list__show-more" onClick={this.increaseAmountOfCards}>{amountCard >= 8 ?'Скрыть':'Показать больше'}</button>
                </div>)
    };
};
